import{C as t,I as e}from"./CountryView.e4430d7c.js";import{d as i,e as l,w as r,o as d,f as n,b as o,a}from"./index.74522e6a.js";const s=o(" Azoren "),g=o(' Midden in de Atlantische Oceaan, niet helemaal halfweg tussen Lissabon en New-York, liggen negen kleine eilanden: de Azoren. Vroeger werden ze de Vlaamse Eilanden genoemd; ze werden immers in de 15de eeuw gekoloniseerd door Portugezen en Vlamingen. Nu zijn de Azoren vooral gekend vanwege hun hogedrukgebieden die ons weer be\xEFnvloeden! Uiteraard hebben de negen eilanden gemeenschappelijke kenmerken. De vele vulkaankegels en de fascinerende kratermeren verraden hun vulkanische oorsprong. Meestal omhullen mist en wolken het groene land. De gematigde temperatuur en de grote vochtigheid maken van de Azoren een prachtige botanische tuin. De bevolking van deze Portugese eilanden is grotendeels katholiek. Religieuze manifestaties bekleden nog steeds een belangrijke plaats in het dagelijks leven. De feesten van de "Espirito Santo" kwamen via Portugal naar de Azoren. Deze liefdadigheidsfeesten waren noodzakelijk toen overleven op de Azoren niet gemakkelijk was. Het uitdelen van voedsel aan behoeftigen is nu nog een zeer belangrijk aspect van deze feesten. Toch heeft elk eiland ook nog een eigen gezicht. '),m=a("br",null,null,-1),c=a("br",null,null,-1),u=o(' Santa Maria is weinig vulkanisch en heeft enkele zandstranden. Evenals Graciosa, het eiland met de Vlaamse windmolens, geniet het van een relatief droog en zonnig klimaat. Prins Albert van Monaco bezocht het eiland Graciosa in 1879. Hij noemde de zwavelgrot "furna do enxofre" een wereldwonder. '),h=a("br",null,null,-1),k=a("br",null,null,-1),z=o(' Flores en het piepkleine Corvo zijn zeer vochtige eilanden. Ze kregen terecht de naam "bloemeneilanden". Door slechte weersomstandigheden kunnen beide eilanden soms wekenlang ge\xEFsoleerd zijn. '),_=a("br",null,null,-1),v=a("br",null,null,-1),p=o(" Pico betekent top en die top is een vulkaankegel van 2351 meter, het hoogste punt van Portugal. De klim naar de top voor zonsopgang is absoluut een aanrader. Zeilers die de Atlantische Oceaan oversteken, kennen de haven van Horta, hoofdstad van Faial. De laatste vulkaanuitbarsting op de Azoren begon in 1957 en duurde meer dan een jaar. Faial kreeg hierdoor 2,4 km2 nieuw land bij. "),b=a("br",null,null,-1),w=a("br",null,null,-1),f=o(' Op S\xE3o Jorge maken ze heerlijke kaas naar een recept dat de Vlamingen in de 15\xB0 eeuw meebrachten. Je kan er prachtige wandelingen maken van faja tot faja. Terceira biedt je de gelegenheid een "tourada a corda" mee te maken. In elk dorpje wordt deze geanimeerde stierenkamp zonder verliezer in de smalle straatjes gehouden. Het historisch centrum van Angra is in 1983 door de UNESCO tot werelderfgoed uitgeroepen. '),j=a("br",null,null,-1),y=a("br",null,null,-1),A=o(" Het groene S\xE3o Miguel is het grootste eiland. De landschappen zijn er sterk gevarieerd. Men kan er zich in de Auvergne of in Ierland wanen, en zelfs in IJsland of op Corsica. Welk eiland is nu het mooiste? Kom en oordeel zelf! "),M=i({__name:"AzorenView",setup(x){return(V,D)=>(d(),l(t,null,{title:r(()=>[s]),images:r(()=>[n(e,{country:"azoren","image-name":"angra"}),n(e,{country:"azoren","image-name":"baai"}),n(e,{country:"azoren","image-name":"caldera"}),n(e,{country:"azoren","image-name":"capelinhos"}),n(e,{country:"azoren","image-name":"cidades"}),n(e,{country:"azoren","image-name":"dorpskerk"}),n(e,{country:"azoren","image-name":"feest"}),n(e,{country:"azoren","image-name":"graciosa"}),n(e,{country:"azoren","image-name":"kaas"}),n(e,{country:"azoren","image-name":"kratermeer"}),n(e,{country:"azoren","image-name":"lagoa"}),n(e,{country:"azoren","image-name":"molens"}),n(e,{country:"azoren","image-name":"pico"}),n(e,{country:"azoren","image-name":"weiden"}),n(e,{country:"azoren","image-name":"woning"})]),"long-text":r(()=>[g,m,c,u,h,k,z,_,v,p,b,w,f,j,y,A]),_:1}))}});export{M as default};
