import{C as o,I as e}from"./CountryView.e4430d7c.js";import{d as r,e as s,w as i,o as d,f as n,b as a,a as t}from"./index.74522e6a.js";const l=a(" Baltische Staten "),u=a(' Estland, Letland en Litouwen... de Baltische Staten, een naam die verwijst naar de periode onder de heerschappij van het Russische Keizerrijk. Na de oktoberrevolutie in 1917 en de burgeroorlog die daar op volgde, konden de Baltische staten zich honderd jaar geleden, in 1918, onafhankelijk verklaren. Na de Tweede Wereldoorlog worden de Baltische staten door de Sovjetunie bezet. De russificatie wordt een periode van terreur door de KGB. Van de bevolking kwam er geweldloos verzet, de "Zingende Revolutie". Het hoogtepunt, in 1989, was de \u201CBaltische Weg", een mensenketting, van Vilnius, over Riga, tot Tallinn, 600 km lang, twee miljoen deelnemers. Het resulteerde in het herstel van hun onafhankelijk in 1991. In 2004 traden de landen toe tot de EU en de NAVO, in 2007 tot de Schengenzone. Enkele jaren later voerden ze de Euro in. De historische centra van de drie hoofdsteden staan op de lijst van UNESCO werelderfgoed. '),h=t("br",null,null,-1),c=t("br",null,null,-1),g=a(" Litouwen, twee maal Belgi\xEB, is het grootse van de drie landen. Niet ver van de hoofdstad Vilnius bevindt zich het geografisch centrum van Europa. Grootvorstendom Litouwen kende de grootste bloei in de 15de eeuw onder Vytautas de Grote. De belangrijkste steden zijn Vilnius, Kaunas, Klaip\xE9da en \u0160iauliai, bekend door de aangrijpende Kruisenheuvel. Van de vele historische gebouwen verdienen de middeleeuwse burchten van Trakai en Kaunas onze aandacht. Het Nationaal Park \u201Cde Koerse Schoorwal\u201D , is eveneens UNESCO erfgoed. Nobelprijswinnaar Thomas Mann had er een zomerverblijf. In het Nationaal Park \u017Demaitija verborgen de Sovjets hun lanceerbasis voor kernkoppen die gericht waren op de grote westerse steden. "),m=t("br",null,null,-1),v=t("br",null,null,-1),b=a(" Letland is iets kleiner dan Litouwen. De hoofdstad Riga was, als bloeiende Hanzehaven, een rijke stad. Begin 20ste eeuw bouwde men nieuwe wijken met gebouwen in schitterende art nouveau stijl. Mikhail Eisenstein was een van de belangrijke architecten. Het noorden van Letland behoorde tot het vroegere Lijfland, een gebied met een eigen taal en cultuur. In de kuststreek rond Mazirbe houdt men de Lijfse cultuur nog levendig met een jaarlijks festival. Een mooi natuurgebied, met tal van recreatiemogelijkheden, is het Gaujas Nationaal Park. Daar situeert zich de legende rond \u201CDe roos van Turaida\u201D. "),k=t("br",null,null,-1),w=t("br",null,null,-1),B=r({__name:"BaltischeStatenView",setup(_){return(j,p)=>(d(),s(o,null,{title:i(()=>[l]),images:i(()=>[n(e,{country:"baltischestaten","image-name":"kathedraal"}),n(e,{country:"baltischestaten","image-name":"waterburcht"}),n(e,{country:"baltischestaten","image-name":"kruisenheuvel"}),n(e,{country:"baltischestaten","image-name":"lijvenfeest"}),n(e,{country:"baltischestaten","image-name":"park"}),n(e,{country:"baltischestaten","image-name":"riga"}),n(e,{country:"baltischestaten","image-name":"vuurtoren"}),n(e,{country:"baltischestaten","image-name":"tallinn"}),n(e,{country:"baltischestaten","image-name":"molenmuseum"})]),"long-text":i(()=>[u,h,c,g,m,v,b,k,w]),_:1}))}});export{B as default};
