import{C as t,I as e}from"./CountryView.e4430d7c.js";import{d as o,e as d,w as i,o as s,f as n,b as r,a}from"./index.74522e6a.js";const l=r(" Madeira "),u=r(` MADEIRA, parel in de Atlantische Oceaan, drijvende tuin, bloemenparadijs... Deze omschrijvingen dankt het eiland aan zijn mild klimaat: 's winters is het er nooit te koud en 's zomers nooit te warm. Bovendien heeft Madeira een vruchtbare bodem en voldoende water. Toen het eiland in de 15de eeuw gekoloniseerd werd onder impuls van Hendrik De Zeevaarder, was het dicht begroeid met bomen en struiken, vandaar de naam "Madeira", wat "hout" betekent. Madeira werd een druk centrum waar suiker en kleurstoffen verhandeld werden. Vlaanderen was \xE9\xE9n van zijn belangrijkste handelspartners. Suiker werd geruild tegen kunstwerken. In musea en kerken tref je nu nog Vlaamse schilderijen en beeldhouwwerken uit de 16de eeuw aan. `),m=a("br",null,null,-1),g=a("br",null,null,-1),c=r(" Madeira heeft steeds gepoogd om economisch onafhankelijk te zijn van Portugal. Op de berghellingen zijn terrassen aangelegd om aan landbouw te doen. Een uitgebreid netwerk van kanalen zorgt voor de aanvoer van water. Van suikerriet maakt men nog steeds suikerriethoning voor de speciale honingcake. En vergeten we de wereldberoemde Madeirawijn niet. Er zijn ook twee bloeiende huisindustrie\xEBn: het borduren van linnen en het vlechten van wilgentenen tot gebruiks- en siervoorwerpen\u2026 "),h=a("br",null,null,-1),k=a("br",null,null,-1),v=r(" Toerisme is de grootste bron van inkomsten voor Madeira. Het is een eiland met gastvrije en vriendelijke mensen, met een prachtige natuur en een paradijselijke begroeiing. Er wordt druk gewerkt aan de uitbreiding van de luchthaven en aan de bouw van nieuwe hotels. Madeira is een paradijs voor wandelaars. Vooral wandelingen langs de levada 's zijn er populair. En je kan er ook bergwandelingen maken. De tocht van de Pico Ruivo naar de Pico do Arieiro is een topper. "),w=a("br",null,null,-1),p=a("br",null,null,-1),_=r(" Naast Madeira, op een 40 -tal kilometer naar het noordoosten toe, ligt er een kleiner eiland: PORTO-SANTO. Dit eiland werd een jaar vroeger ontdekt dan Madeira. Porto-Santo is klein en vrij vlak, op enkele pieken na. Die vulkaantoppen bieden je mooie vergezichten over het eiland. De begroeiing is er eerder schraal. De overheersende kleur is oker, de kleur van de woestijn. Tijdens de vochtige wintermaanden ziet het landschap er tijdelijk groen uit. De grootste troef van Porto-Santo is ongetwijfeld het prachtige zandstrand. Aan het gouden zand worden wonderlijke krachten toegeschreven. De vrij stabiele temperatuur op Porto-Santo, de geringe neerslag, het gouden zand en het blauwe water maken van dit eiland een paradijs voor zonnekloppers. "),b=a("br",null,null,-1),j=a("br",null,null,-1),E=o({__name:"MadeiraView",setup(f){return(z,y)=>(s(),d(t,null,{title:i(()=>[l]),images:i(()=>[n(e,{country:"madeira","image-name":"basaltzuilen"}),n(e,{country:"madeira","image-name":"camara"}),n(e,{country:"madeira","image-name":"druivenoogst"}),n(e,{country:"madeira","image-name":"folklore"}),n(e,{country:"madeira","image-name":"funchal"}),n(e,{country:"madeira","image-name":"kunst"}),n(e,{country:"madeira","image-name":"levada"}),n(e,{country:"madeira","image-name":"maderia"}),n(e,{country:"madeira","image-name":"porto"}),n(e,{country:"madeira","image-name":"ruivo"}),n(e,{country:"madeira","image-name":"santana"}),n(e,{country:"madeira","image-name":"santo"})]),"long-text":i(()=>[u,m,g,c,h,k,v,w,p,_,b,j]),_:1}))}});export{E as default};
